custom_css:
/* Solarized Light Theme for SillyTavern - Complete CSS */

/* Form Controls and Dropdowns */
select, .form-control, .ui-widget-content {
    background-color: #fdf6e3 !important;
    color: #2c3e50 !important;
}

input[type="text"], input[type="search"], textarea, .form-control {
    background-color: #eee8d5 !important;
    color: #2c3e50 !important;
    border: 1px solid #93a1a1 !important;
}

input[type="number"] {
    background-color: #eee8d5 !important;
    color: #2c3e50 !important;
    border: 1px solid #93a1a1 !important;
}

/* Select2 Dropdown Components */
.select2-container .select2-selection {
    background-color: #eee8d5 !important;
    color: #2c3e50 !important;
    border: 1px solid #93a1a1 !important;
}

.select2-container .select2-selection__rendered {
    color: #2c3e50 !important;
}

.select2-dropdown {
    background-color: #eee8d5 !important;
    border: 1px solid #93a1a1 !important;
}

/* Section Headers */
h4 {
    background: #eee8d5 !important;
    color: #2c3e50 !important;
}

.standoutHeader {
    background: #eee8d5 !important;
    color: #2c3e50 !important;
}

/* Chat Area */
#chat {
    padding-left: 0.5% !important;
    padding-right: 0.5% !important;
    border: none !important;
    margin-bottom: 0px !important;
    margin-top: 0px !important;
}

/* Message Buttons - Remove Halos */
.mes_buttons button, 
.mes_buttons .fa-solid,
.mes_buttons i {
    box-shadow: none !important;
    text-shadow: none !important;
    filter: none !important;
}

.mes_edit_buttons button {
    opacity: 1 !important;
    filter: none !important;
    background-color: rgba(238, 232, 213, 0.8) !important;
    border: 1px solid #93a1a1 !important;
    color: #2c3e50 !important;
}

.mes_edit_buttons {
    filter: drop-shadow(0px 0px 0px black);
}

/* CSS Custom Properties */
:root {
    --okGreen70a: #22c55e !important;
}

/* Send Form Styling */
#form_sheld {
    padding-left: 0.5% !important;
    padding-right: 0.5% !important;
    border-top: 1px solid #93a1a1 !important;
    padding-top: 2px !important;
}

#send_form {
    border: 1px solid #93a1a1 !important;
    border-radius: 10px 10px 10px 10px !important;
}

#send_form:focus-within {
    border-radius: 10px 10px 10px 10px !important;
    border: 1px solid #93a1a1 !important;
}

/* Prompt Manager */
#completion_prompt_manager_list {
    background-color: #fdf6e3 !important;
}

.completion_prompt_manager_header span {
    color: #2c3e50 !important;
    text-shadow: none !important;
    filter: none !important;
}

.completion_prompt_manager_header * {
    font-weight: bold !important;
}

/* Top Bar */
#top-bar {
    box-shadow: none !important;
    border-bottom: 1px solid #93a1a1 !important;
    padding-bottom: 36px !important;
}

/* General Icon Cleanup */
.fa-solid {
    text-shadow: none !important;
    filter: none !important;
    box-shadow: none !important;
}
#left-nav-panel {
    position: fixed !important;
    left: 0 !important;
    top: 36px !important; /* Your top bar height */
    width: 375px !important;
    height: calc(100vh - 2px - var(--form-height, 80px)) !important;
    overflow-y: auto !important;
    z-index: 1000 !important;
    border-radius: 0px !important;
    border: 1px solid #93a1a1 !important;
}
